"use strict";

// Slider People
new Splide('#people_slider', {
  type: 'loop',
  perMove: 1,
  perPage: 1
}).mount(); // Counters

function animate(obj, initVal, lastVal, duration) {
  let startTime = null;
  let currentTime = Date.now();

  const step = currentTime => {
    if (!startTime) {
      startTime = currentTime;
    }

    const progress = Math.min((currentTime - startTime) / duration, 1);
    obj.innerHTML = Math.floor(progress * (lastVal - initVal) + initVal);

    if (progress < 1) {
      window.requestAnimationFrame(step);
    } else {
      window.cancelAnimationFrame(window.requestAnimationFrame(step));
    }
  };

  window.requestAnimationFrame(step);
}

const counter01 = document.getElementById('counter_01');
const counter02 = document.getElementById('counter_02');

let counter01Start = function () {
  let executed = false;
  return function () {
    if (!executed) {
      executed = true;
      animate(counter01, 0, 470, 1500);
      animate(counter02, 0, 100, 500);
    }
  };
}(); // Scroll Animations


const animItems = document.querySelectorAll('.animItem');

if (animItems.length > 0) {
  window.addEventListener('scroll', animOnScroll);

  function animOnScroll(params) {
    for (let i = 0; i < animItems.length; i++) {
      const animItem = animItems[i];
      const animItemHeight = animItem.offsetHeight;
      const animItemOffset = offset(animItem).top;
      const animStart = 4;
      let animItemPoint = window.innerHeight - animItemHeight / animStart;

      if (animItemHeight > window.innerHeight) {
        animItemPoint = window.innerHeight - window.innerHeight / animStart;
      }

      if (pageYOffset > animItemOffset - animItemPoint && pageYOffset < animItemOffset + animItemHeight) {
        animItem.classList.add('active');
        counter01Start();
      } else {
        if (!animItem.classList.contains('noRepeat')) {
          animItem.classList.remove('active');
        }
      }
    }
  }

  function offset(el) {
    const rect = el.getBoundingClientRect(),
          scrollLeft = window.pageXOffset || document.documentElement.scrollLeft,
          scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    return {
      top: rect.top + scrollTop,
      left: rect.left + scrollLeft
    };
  }

  setTimeout(() => {
    animOnScroll();
  }, 400);
}